import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import Header from './components/header';
import Footer from './components/footer';
import './components/Prediction.css';

const Prediction = () => {
  const [selectedQuestion, setSelectedQuestion] = useState('');
  const [answer, setAnswer] = useState('');
  const [loading, setLoading] = useState(false);
  const [chartData, setChartData] = useState(null);

  const questions = [
    "Predicted seat count for BJP",
    "Seat changes compared to previous election",
    "Party likely to win most seats",
    "BJP maintaining majority",
    "Congress gaining more seats",
    "Party likely to experience decline in support",
    "Chance of coalition government forming",
    "Party with highest probability of forming government",
    "Expected overall performance of each party",
    "Predicted change in seat count for each party",
  ];

  useEffect(() => {
    axios.get('/predicted_seat_counts.json')
      .then((response) => {
        const data = response.data;
        setChartData({
          labels: Object.keys(data),
          datasets: [{
            label: 'Seat Distribution',
            data: Object.values(data),
            backgroundColor: [
              '#FF6384',
              '#36A2EB',
              '#FFCE56',
              '#FF9F40',
              '#FFCD56',
              '#4BC0C0',
              '#9966FF',
              '#FF6384'
            ],
            hoverBackgroundColor: [
              '#FF6384',
              '#36A2EB',
              '#FFCE56',
              '#FF9F40',
              '#FFCD56',
              '#4BC0C0',
              '#9966FF',
              '#FF6384'
            ]
          }]
        });
      })
      .catch((error) => console.error('Error loading chart data:', error));
  }, []);

  const fetchAnswer = async (question) => {
    setLoading(true);
    try {
      const response = await axios.post('http://127.0.0.1:5000/api/predict', {
        question: question,
      });
      setAnswer(response.data.answer);
    } catch (error) {
      console.error('Error fetching prediction:', error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      <Header />
      <div style={{ flex: '1', display: 'flex', flexDirection: 'column', width: '70%', justifyContent: 'center', alignItems: 'center', position: 'relative', marginLeft: '15%' }}>
        {chartData && (
          <div className="chart-container">
            <Pie data={chartData} />
          </div>
        )}
        <div className="prediction-container">
          <h2>Select a Prediction Question</h2>
          <select
            value={selectedQuestion}
            onChange={(e) => setSelectedQuestion(e.target.value)}
            className="question-dropdown"
          >
            <option value="">Select a question...</option>
            {questions.map((question, index) => (
              <option key={index} value={question}>{question}</option>
            ))}
          </select>
          <button
            onClick={() => fetchAnswer(selectedQuestion)}
            className="btn btn-primary"
            disabled={!selectedQuestion || loading}
          >
            Get Prediction
          </button>
          {loading && <div className="loading">Loading...</div>}
          {answer && <div className="answer" dangerouslySetInnerHTML={{ __html: answer }} />}
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Prediction;
